UPDATE smartKPI set [isPartOK] = ISNULL([[isPartOK]], 0), [PartNumber] = [[PartNumber]], [SerialNumber] = [[SerialNumber]], [OrderNumber] = [[OrderNumber]], [ScrapReason] = substring([[ScrapReason]],0,254), [isUpdated] = 1, [numberOfParts] = CAST([[NumberOfParts]] as int), [confirmToSAP] = isnull([[confirmToSAP]],0), [confirmedToSAP] = isnull([[confirmedToSAP]],0), [SAPOperationNumber] = [[SAPOperationNumber]], [SAPconfirmationResult] = [[SAPconfirmationResult]], [timePerPartSec] = [[timePerPartSec]], [JSON] = [[JSON]] where ProductionTime = [[DateTime]] and Machine = convert(varchar(255), [[Machine]]) and Station = convert(varchar(255), [[Station]]);

