insert into shiftCalendar (Plant, Machine, StartTime, EndTime, Qualifier, Name, Machine_Full_Name, Utilization) select [[plant]], SUBSTRING([[machine]],2, CHARINDEX('_'+[[plant]]+'_',[[machine]])-2), [[StartTime]], [[EndTime]], [[Qualifier]], [[Name]], [[machine]], [[utilization]] where [[machine]] is not null and CHARINDEX('_'+[[plant]]+'_',[[machine]]) > 1 and LEN([[machine]]) > 1 union select [[plant]], [[machine]], [[StartTime]], [[EndTime]], [[Qualifier]], [[Name]], [[machine]], [[utilization]] where [[machine]] is null or CHARINDEX('_'+[[plant]]+'_',[[machine]]) <= 1 or LEN([[machine]]) <= 1;
--commit transaction;


