// #0. case of 0 record
try {
	var offset;
	if (TimeLosses === undefined || TimeLosses === null) {
//		// result: INFOTABLE dataShape: "KBTimeLossesListDataShape"
//		TimeLosses = me.GetMachineDowntimeData({
