											name: 'KBMaschStatus.1.' + row.Type
										});
									} catch (err) {
										StatusValue = row.Type;
									}
									baseTable.rows[xx].Status = StatusValue;
									Label = StatusValue + ", ";
									// result=result+baseTable.rows[xx].Type+ "-"+xx+",";
								} else {
									if (Group == 'L1') {
										try {

											StatusValue = LocalizationTables[MyLanguage].GetToken({
												name: row.Status
											});
